import store, { createEffect } from "."

const [setNumber, getNumber] = store(1)
const [setDouble, getDouble] = store(2)

createEffect(() => {
    const number = getNumber()
    const double = getDouble()
    
    setDouble(number * 2)

    console.log({number})
    console.log({double})

}, [getNumber, getDouble])

setNumber(4)
setDouble(5)