export default class PointLightMixin {
    get decay() {
        return this.object3d.decay;
    }
    set decay(val) {
        this.object3d.decay = val;
    }
    get distance() {
        return this.object3d.distance;
    }
    set distance(val) {
        this.object3d.distance = val;
    }
    get power() {
        return this.object3d.power;
    }
    set power(val) {
        this.object3d.power = val;
    }
}
