import { CubeTextureLoader } from "three";
import { forceGet } from "@lincode/utils";
import { increaseLoadingCount, decreaseLoadingCount } from "../../../states/useLoadingCount";
const cache = new Map();
const loader = new CubeTextureLoader();
export default (urls) => forceGet(cache, urls.sort().join(","), () => {
    increaseLoadingCount();
    return loader.load(urls, () => decreaseLoadingCount(), undefined, () => decreaseLoadingCount());
});
