import { World, GSSolver, SplitSolver, NaiveBroadphase, Material, ContactMaterial } from "cannon-es";
export const world = new World();
world.gravity.set(0, -20, 0);
world.quatNormalizeSkip = 0;
world.quatNormalizeFast = false;
const solver = new GSSolver();
world.defaultContactMaterial.contactEquationStiffness = 1e9;
world.defaultContactMaterial.contactEquationRelaxation = 4;
solver.iterations = 7;
solver.tolerance = 0.1;
const split = true;
if (split)
    world.solver = new SplitSolver(solver);
else
    world.solver = solver;
world.broadphase = new NaiveBroadphase();
export const [defaultMaterial] = world.defaultContactMaterial.materials;
export const slipperyMaterial = new Material("slipperyMaterial");
world.addContactMaterial(new ContactMaterial(slipperyMaterial, slipperyMaterial, {
    friction: 0.0, restitution: 0.0
}));
world.addContactMaterial(new ContactMaterial(slipperyMaterial, defaultMaterial, {
    friction: 0.001, restitution: 0.0
}));
