import { background, rendering, settings } from "../../.."
import { SetupNode } from "./types"

export default (node: Partial<SetupNode>) => {
    settings.defaultFog = node.defaultFog ?? settings.defaultFog
    settings.defaultLight = node.defaultLight ?? settings.defaultLight

    rendering.toneMapping = node.toneMapping ?? rendering.toneMapping
    rendering.exposure = node.exposure ?? rendering.exposure
    rendering.bloom = node.bloom ?? rendering.bloom
    rendering.bloomStrength = node.bloomStrength ?? rendering.bloomStrength
    rendering.bloomRadius = node.bloomRadius ?? rendering.bloomRadius
    rendering.bloomThreshold = node.bloomThreshold ?? rendering.bloomThreshold
    rendering.bokeh = node.bokeh ?? rendering.bokeh
    rendering.bokehFocus = node.bokehFocus ?? rendering.bokehFocus
    rendering.bokehMaxBlur = node.bokehMaxBlur ?? rendering.bokehMaxBlur
    rendering.bokehAperture = node.bokehAperture ?? rendering.bokehAperture
    rendering.ambientOcclusion = node.ambientOcclusion ?? true

    background.texture = node.texture ?? background.texture
    background.skybox = node.skybox ?? background.skybox
    background.color = node.color ?? background.color
}